import * as React from 'react';
import { Typography } from '@mui/material';
import './Notification.scss';
import Checkbox from '@mui/material/Checkbox';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import Stack from '@mui/material/Stack';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

// Custom style table 
const StyledTableCell = styled(TableCell)(({ theme }) => ({
    [`&.${tableCellClasses.head}`]: {
        backgroundColor: '#eee',
        fontWeight: 'bold'
    },
    [`&.${tableCellClasses.body}`]: {
        fontSize: 14,
    },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
    '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
    },
    // hide last border
    '&:last-child td, &:last-child th': {
        border: 0,
    },
}));


const notifications = [
    {
        no: 1,
        postedDate: "14-Apr-2022",
        postedBy: "	To To Phan",
        content: "DOC100191 has just updated a new revision follow to the new change of the 24h Meeting. Everyone read it and follows the procedure until we get the new document replaces. Any feedback, comments, or questions you can contact me via chat or post in the P&E.",
        deadline: "18-Apr-2022"
    },
    {
        no: 1,
        postedDate: "14-Apr-2022",
        postedBy: "	To To Phan",
        content: "DOC100191 has just updated a new revision follow to the new change of the 24h Meeting. Everyone read it and follows the procedure until we get the new document replaces. Any feedback, comments, or questions you can contact me via chat or post in the P&E.",
        deadline: "18-Apr-2022"
    },
    {
        no: 1,
        postedDate: "14-Apr-2022",
        postedBy: "	To To Phan",
        content: "DOC100191 has just updated a new revision follow to the new change of the 24h Meeting. Everyone read it and follows the procedure until we get the new document replaces. Any feedback, comments, or questions you can contact me via chat or post in the P&E.",
        deadline: "18-Apr-2022"
    },
    {
        no: 1,
        postedDate: "14-Apr-2022",
        postedBy: "	To To Phan",
        content: "DOC100191 has just updated a new revision follow to the new change of the 24h Meeting. Everyone read it and follows the procedure until we get the new document replaces. Any feedback, comments, or questions you can contact me via chat or post in the P&E.",
        deadline: "18-Apr-2022"
    },
    {
        no: 1,
        postedDate: "14-Apr-2022",
        postedBy: "	To To Phan",
        content: "DOC100191 has just updated a new revision follow to the new change of the 24h Meeting. Everyone read it and follows the procedure until we get the new document replaces. Any feedback, comments, or questions you can contact me via chat or post in the P&E.",
        deadline: "18-Apr-2022"
    },
]

const NotificationPage = () => {
    return (
        <div>
            <Typography sx={{ fontSize: '1.5rem', color: '#185382', margin: '2rem 0 1rem 1rem' }}>Notifications</Typography>
            <TableContainer component={Paper}>
                <Table stickyHeader aria-label="sticky table" sx={{ minWidth: 650 }}>
                    <TableHead>
                        <TableRow>
                            <StyledTableCell>Doc.Id</StyledTableCell>
                            <StyledTableCell align="right">No.</StyledTableCell>
                            <StyledTableCell align="right">Posted Date</StyledTableCell>
                            <StyledTableCell align="right">Posted By</StyledTableCell>
                            <StyledTableCell align="right">Content</StyledTableCell>
                            <StyledTableCell align="right">Deadline</StyledTableCell>
                            <StyledTableCell align="right">Who Have Not Confirmed Yet</StyledTableCell>
                            <StyledTableCell align="right">Confirm</StyledTableCell>
                            <StyledTableCell align="right">Edit/Remove</StyledTableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {notifications.map((row, index) => (
                            <StyledTableRow
                                key={index}
                                hover
                            // sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                            >
                                <StyledTableCell align="left">{row.no}</StyledTableCell>
                                <StyledTableCell align="right">{row.postedDate}</StyledTableCell>
                                <StyledTableCell align="right">{row.postedBy}</StyledTableCell>
                                <StyledTableCell align="right">{row.content}</StyledTableCell>
                                <StyledTableCell align="right">{row.deadline}</StyledTableCell>
                                <StyledTableCell align="right"><img src="./Image/Logo/icon.png" alt="" /></StyledTableCell>
                                <StyledTableCell align="right"> <Checkbox defaultChecked /></StyledTableCell>
                                <StyledTableCell align="right">
                                    <Stack direction="row" justifyContent="center" alignItems="center" spacing={0.5}>
                                        <EditIcon color='primary' />
                                        <DeleteIcon color='error' />
                                    </Stack>
                                </StyledTableCell>
                            </StyledTableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div >
    );
}
export default NotificationPage
